tring","name":"targetChain","type":"string"},{"internalType":"string","name":"targetToken","type":"string"},{"internalType":"string","name":"targetRecipient","type":"string"},{"components":[{"internalType":"uint256","name":"windowMinutes","type":"uint256"},{"internalType":"uint256","name":"executionIntervals","type":"uint256"},{"internalType":"uint256","name":"maxSlippage","type":"uint256"},{"internalType":"uint256","name":"minFillSize","type":"uint256"},{"internalType":"bool","name":"enableMEVProtection","type":"bool"}],"internalType":"struct IFlowFusionBridge.TWAPConfig","name":"twapConfig","type":"tuple"},{"internalType":"bytes32","name":"htlcHash","type":"bytes32"},{"internalType":"uint256","name":"timeoutHeight","type":"uint256"},{"internalType":"uint256","name":"createdAt","type":"uint256"},{"internalType":"uint256","name":"executedAmount","type":"uint256"},{"internalType":"uint256","name":"lastExecution","type":"uint256"},{"internalType":"enum IFlowFusionBridge.OrderStatus","name":"status","type":"uint8"},{"internalType":"uint256","name":"averagePrice","type":"uint256"}],"internalType":"struct IFlowFusionBridge.TWAPOrder","name":"","type":"tuple"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"user","type":"address"}],"name":"getUserOrders","outputs":[{"internalType":"bytes32[]","name":"","type":"bytes32[]"}],"stateMutability":"view","type":"function"}],"devdoc":{"kind":"dev","methods":{},"title":"IFlowFusionBridge","version":1},"userdoc":{"kind":"user","methods":{"cancelOrder(bytes32)":{"notice":"Cancel an order and refund remaining tokens"},"claimHTLC(bytes32,bytes32)":{"notice":"Claim HTLC with secret to complete cross-chain swap"},"createTWAPOrder(address,uint256,string,string,string,(uint256,uint256,uint256,uint256,bool),bytes32,uint256)":{"notice":"Create a new TWAP order for cross-chain execution"},"executeTWAPInterval(bytes32,uint256,uint256,bytes)":{"notice":"Execute a TWAP interval for an order"}},"notice":"Interface for FlowFusion Multi-Chain TWAP Bridge","version":1}},"settings":{"compilationTarget":{"contracts/interfaces/IFlowFusionBridge.sol":"IFlowFusionBridge"},"evmVersion":"paris","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[],"viaIR":true},"sources":{"contracts/interfaces/IFlowFusionBridge.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title IFlowFusionBridge
 * @notice Interface for FlowFusion Multi-Chain TWAP Bridge
 */
interface IFlowFusionBridge {
    
    /*//////////////////////////////////////////////////////////////
                                STRUCTS
    //////////////////////////////////////////////////////////////*/
    
    struct TWAPConfig {
        uint256 windowMinutes;      // TWAP calculation window (5-1440 minutes)
        uint256 executionIntervals; // Number of execution intervals (2-20)
        uint256 maxSlippage;        // Maximum slippage in basis points (1-1000)
        uint256 minFillSize;        // Minimum fill size per interval
        bool enableMEVProtection;   // Enable MEV protection
    }
    
    struct TWAPOrder {
        bytes32 id;                 // Unique order identifier
        address user;               // Order creator
        address sourceToken;        // Source token address (address(0) for ETH)
        uint256 sourceAmount;       // Total source amount
        string targetChain;         // Target blockchain identifier
        string targetToken;         // Target token identifier
        string targetRecipient;     // Recipient address on target chain
        TWAPConfig twapConfig;      // TWAP execution configuration
        bytes32 htlcHash;          // Hash for HTLC mechanism
        uint256 timeoutHeight;      // Block height timeout
        uint256 createdAt;          // Creation timestamp
        uint256 executedAmount;     // Amount already executed
        uint256 lastExecution;      // Last execution timestamp
        OrderStatus status;         // Current order status
        uint256 averagePrice;       // Weighted average execution price
    }
    
    struct ExecutionRecord {
        uint256 timestamp;          // Execution timestamp
        uint256 amount;            // Amount executed
        uint256 price;             // Execution price
        uint256 gasUsed;           // Gas used for execution
        uint256 slippage;          // Slippage in basis points
    }
    
    enum OrderStatus {
        Executing,      // Order is being executed
        Completed,      // All intervals executed
        Cancelled,      // Order cancelled by user
        Expired,        // Order expired (timeout reached)
        Claimed         // HTLC claimed (cross-chain completed)
    }

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/
    
    event OrderCreated(
        bytes32 indexed orderId,
        address indexed user,
        string targetChain,
        address sourceToken,
        uint256 sourceAmount,
        string targetToken,
        TWAPConfig twapConfig
    );

    event TWAPExecution(
        bytes32 indexed orderId,
        uint256 intervalNumber,
        uint256 executedAmount,
        uint256 averagePrice,
        uint256 timestamp
    );

    event OrderCompleted(
        bytes32 indexed orderId,
        uint256 totalExecuted,
        uint256 averagePrice
    );

    event OrderCancelled(
        bytes32 indexed orderId,
        address indexed user,
        uint256 refundAmount
    );

    event HTLCCreated(
        bytes32 indexed orderId,
        bytes32 indexed htlcHash,
        uint256 amount,
        uint256 timeoutHeight
    );

    event HTLCClaimed(
        bytes32 indexed orderId,
        bytes32 secret,
        address claimer
    );

    /*//////////////////////////////////////////////////////////////
                            MAIN FUNCTIONS
    //////////////////////////////////////////////////////////////*/
    
    /**
     * @notice Create a new TWAP order for cross-chain execution
     */
    function createTWAPOrder(
        address sourceToken,
        uint256 sourceAmount,
        string memory targetChain,
        string memory targetToken,
        string memory targetRecipient,
        TWAPConfig memory twapConfig,
        bytes32 htlcHash,
        uint256 timeoutHeight
    ) external payable returns (bytes32 orderId);

    /**
     * @notice Execute a TWAP interval for an order
     */
    function executeTWAPInterval(
        bytes32 orderId,
        uint256 intervalAmount,
        uint256 executionPrice,
        bytes calldata priceProof
    ) external;

    /**
     * @notice Cancel an order and refund remaining tokens
     */
    function cancelOrder(bytes32 orderId) external;

    /**
     * @notice Claim HTLC with secret to complete cross-chain swap
     */
    function claimHTLC(bytes32 orderId, bytes32 secret) external;

    /*//////////////////////////////////////////////////////////////
                            VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/
    
    function getOrder(bytes32 orderId) external view returns (TWAPOrder memory);
    function getUserOrders(address user) external view returns (bytes32[] memory);
    function getExecutionHistory(bytes32 orderId) external view returns (ExecutionRecord[] memory);
    function getCurrentTWAPPrice(bytes32 orderId) external view returns (uint256);
    function getNextExecutionTime(bytes32 orderId) external view returns (uint256);
}","keccak256":"0x43f4dfae76e7f0cafeeb31436df057dd90bbf13c22acd22ac639e3dcd8466faa","license":"MIT"}},"version":1}

